import React from 'react'
import './Products.css'
import Slidesshow from './Slidesshow';
import axios from 'axios'
import { useEffect ,useState} from 'react';
import { Link } from 'react-router-dom';
// import {details} from './details';

const Products = () => {
  const [details,setdetails] = useState([]);
  const getproducts=async()=>{
      const res = await axios.get('http://localhost:8000/products');
      console.log(res.data);
      setdetails(res.data);
      
  }
  useEffect(()=>{
    getproducts();
  },[])
  return (
    <div className='whole'>
      <Slidesshow/>
    {details.map((node)=>{
        return(
      <div className='card'>
        <Link to='https://www.amazon.in/dp/B0BKG5PQ6T/ref=s9_acsd_al_bw_c2_x_0_t?pf_rd_m=A1K21FY43GMZF8&pf_rd_s=merchandised-search-10&pf_rd_r=HBMY4W8V70459CXZ9R2K&pf_rd_t=101&pf_rd_p=06433c57-9f58-413c-93bb-42a8cc3e35eb&pf_rd_i=976419031&th=1'>
        <img className = 'image' src={node.thumbnail} alt="" />
        <p className='title'>{node.title}</p>
        <p className='price'><span className='bld'>Price : </span>{node.price}</p>
        <p  className = 'rating' > <span className='bld'>Rating : </span>{node.rating}</p>
        {/* <p className='stock' ><span className='bld'>Stock : </span>{node.stock}</p>           */}
        </Link>
      </div>
        )
    })}
    </div>
  )
}

export default Products
